import axios from "axios";

const API_BASE_URL = `http://localhost:${
  process.env.REACT_APP_API_PORT || "3001"
}/api`; // バックエンドのAPIベースURL

// タスクを取得する関数
export const fetchTasks = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/tasks`);
    return response.data;
  } catch (error) {
    throw new Error("タスクの取得に失敗しました");
  }
};

// タスクを作成する関数
export const createTask = async (taskData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/tasks`, taskData);
    return response.data;
  } catch (error) {
    throw new Error("タスクの作成に失敗しました");
  }
};

// タスクを更新する関数
export const updateTask = async (taskId, taskData) => {
  try {
    const response = await axios.put(
      `${API_BASE_URL}/tasks/${taskId}`,
      taskData
    );
    return response.data;
  } catch (error) {
    throw new Error("タスクの更新に失敗しました");
  }
};

// タスクを削除する関数
export const deleteTask = async (taskId) => {
  try {
    await axios.delete(`${API_BASE_URL}/tasks/${taskId}`);
  } catch (error) {
    throw new Error("タスクの削除に失敗しました");
  }
};

// 認証関連のAPI
export const loginUser = async (credentials) => {
  try {
    const response = await axios.post(
      `${API_BASE_URL}/auth/login`,
      credentials
    );
    return response.data;
  } catch (error) {
    throw new Error("ログインに失敗しました");
  }
};

export const registerUser = async (userData) => {
  try {
    const response = await axios.post(
      `${API_BASE_URL}/auth/register`,
      userData
    );
    return response.data;
  } catch (error) {
    throw new Error("ユーザー登録に失敗しました");
  }
};

// ドキュメント関連のAPI
export const fetchDocuments = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/documents`);
    return response.data;
  } catch (error) {
    throw new Error("ドキュメントの取得に失敗しました");
  }
};

export const createDocument = async (documentData) => {
  try {
    const response = await axios.post(
      `${API_BASE_URL}/documents`,
      documentData
    );
    return response.data;
  } catch (error) {
    throw new Error("ドキュメントの作成に失敗しました");
  }
};

export const updateDocument = async (documentId, documentData) => {
  try {
    const response = await axios.put(
      `${API_BASE_URL}/documents/${documentId}`,
      documentData
    );
    return response.data;
  } catch (error) {
    throw new Error("ドキュメントの更新に失敗しました");
  }
};

export const deleteDocument = async (documentId) => {
  try {
    await axios.delete(`${API_BASE_URL}/documents/${documentId}`);
  } catch (error) {
    throw new Error("ドキュメントの削除に失敗しました");
  }
};
